div.script:
		$(function() {
			$('#logTable').DataTable({
        		"processing": true,
        		"serverSide": true,
        		"ajax": "/polestar/log",
        		"order": [[ 1, "desc" ]],
        		"pageLength": 50,
        		responsive: true,
        		 "columns": [

		            { "data": "level", "orderable": false, "render": function ( data, type, row ) {
                    		if (data==2)
                    		{	return '';
                    		}
                    		else if (data==1)
                    		{	return '';
                    		}
                    		else if (data==0)
                    		{	return '';
                    		}
                    		else
                    		{
                    			return data;
                    		}
                			},
                			"width": "1%"
		            },
		            { "data": "time", "orderable": false, "width": "19%" },
		            { "data": "msg", "orderable": false, "width": "80%", "render": function ( data, type, row ) {
		            	if (row.origin)
		            	{	return '
'+row.origin+" "+data;
		            	}
		            	else
		            	{	return data;
		            	} }
		            }
		        ],
		        "createdRow": function ( row, data, index ) {
            			if ( data.level == 1 ) {
                			$(row).addClass('warning');
            			}
            			else if ( data.level == 0 ) {
                			$(row).addClass('danger');
            			}
        			}
    		});
		});
