script.script:
//execute this webhook with a parameter i.e.
// http://localhost:8080/polestar/scripts/execute/EAC75E961241D889?value=2.0
// it will update the urn:updated:by:webhook sensor with the value

import org.netkernel.mod.hds.*;
import org.netkernel.layer0.nkf.*;

queryParam=context.source("httpRequest:/param/value",String.class);
if (queryParam!=null)
{
	value=Double.parseDouble(queryParam.trim());
	context.sink("arg:state",queryParam);
	context.createResponseFrom("ok").setExpiry(INKFResponse.EXPIRY_ALWAYS);

	m=HDSFactory.newDocument()
		.pushNode("sensors")
		.pushNode("sensor")
		.addNode("id","urn:updated:by:webhook")
		.addNode("value", value );
	req=context.createRequest("active:polestarSensorUpdate");
	req.addArgumentByValue("state",m.toDocument(false));
	context.issueRequest(req);
}
else
{
	state=context.source("arg:state",String.class);
	context.createResponseFrom(state).setExpiry(INKFResponse.EXPIRY_ALWAYS);
}
