script.script:
import org.netkernel.layer0.nkf.*;
lib="""
function drawArea(idx, fillStyle, heightFunction, data, vis)
{	var d = extractArray(data,idx,0);
	vis.add(pv.Area)
		.data(d)
		.interpolate("cardinal")
		.bottom(y(0))
		.height(function(d) { return heightFunction(d)-y(0) } )
		.left(function() { return x(this.index+0.5);} )
		.fillStyle( fillStyle );
}

function drawArea2(idx, fillStyle, strokeStyle, heightFunction, bottomFunction, data, vis)
{	var d = extractArray(data,idx,0);
	vis.add(pv.Area)
		.data(d)
		.interpolate("cardinal")
		.bottom(bottomFunction)
		.height(function(d) { return heightFunction(d)-bottomFunction(d) } )
		.left(function() { return x(this.index+0.5);} )
		.strokeStyle( strokeStyle )
		.fillStyle( fillStyle );
}

function drawLine(idx, fillStyle, dashArray, heightFunction, data, vis)
{	var d = extractArray(data,idx,0);
	vis.add(pv.Line)
		.data(d).interpolate("cardinal")
		.bottom(heightFunction)
		.left(function() {return x(this.index+0.5)})
		.lineWidth(2)
		.strokeDasharray(dashArray)
		.strokeStyle( fillStyle );
//		.add(pv.Dot)
//			.lineWidth(1)
//	    	.fillStyle("white");
}

function drawBar(idx, fillStyle, heightFunction, data, vis)
{	var d=extractArray(gd,idx, 0);
	vis.add(pv.Bar)
		.data(d)
		.bottom(y(0))
		.width(4)
		.height(function(d) { return heightFunction(d)-y(0) })
		.left(function() { return x(this.index);} )
		.fillStyle( fillStyle );
}

function drawBoolean(idx, fillStyle, origin, height, data, vis)
{	var d=extractArray(data,idx, 0);
	vis.add(pv.Area)
		.data(d)
		.interpolate("step-before")
        .bottom(function(d) { return y(d?origin-height/2:origin-height/16); } )
        .height(function(d) { return y(d?height:height/8)-y(0); } )
        .left(function() { return x(this.index+0.5);} )
        .fillStyle( fillStyle );
}

function extractArray(a, idx, defval)
{   result = [];
    for (i=0; i<a.length; i++)
    { b=a[i];
      v=b[idx];
      if (v==null) v=defval;
      result.push(v);
    }
    return result;
}

function getFirstNonNull(a) { result = null; for (i=0; i<a.length; i++) { b=a[i]; if (b!=null) { result=b; break; } } return result; }

""";
resp=context.createResponseFrom(lib);
resp.setMimeType("text/css");
resp.setHeader("HTTP_CACHE_EXPIRES_DELTA_SECONDS",604800L);
