script.script:
import org.netkernel.mod.hds.*;
import org.netkernel.layer0.nkf.*;

m=HDSFactory.newDocument();
m.pushNode("sensors");

m.pushNode("sensor");
m.addNode("id","urn:random1");
m.addNode("value", Math.random());
m.popNode();

h=60000*60*24L;
t=System.currentTimeMillis()%h;
ft=((float)t)/((float)h);
s=Math.sin(ft*Math.PI*2.0);
if (s<0) s=0;

m.pushNode("sensor");
m.addNode("id","urn:ramp");
m.addNode("value", ft);
m.popNode();

m.pushNode("sensor");
m.addNode("id","urn:daylight");
m.addNode("value", s);
m.popNode();


req=context.createRequest("active:polestarSensorUpdate");
req.addArgumentByValue("state",m.toDocument(false));
context.issueRequest(req);
