script.script:
import org.netkernel.mod.hds.*;
import org.netkernel.layer0.nkf.*;

state=context.source("active:polestarSensorState",IHDSDocument.class).getReader();
config=context.source("active:polestarSensorConfig",IHDSDocument.class).getReader();
String msg="Errors on ";
boolean first=true;
for (IHDSReader sensor : state.getNodes("/sensors/sensor[error]"))
{
	id=sensor.getFirstValue("id");
	sensorDef=config.getFirstNode(String.format("key('byId','%s')",id));
	name=sensorDef.getFirstValue("name");
	if (!first) msg=msg+", ";
	first=false;
	msg+=name;
}

if (!first)
{	context.logRaw(INKFLocale.LEVEL_WARNING,"Errors on "+msg);
}
